mapper.select:
select

from user
where id=#{id, jdbcType=INTEGER}

select count(*) 
from user 
where id=#{id}

mapper.update:
update user
set
name=#{name, jdbcType=VARCHAR}, phone=#{phone, jdbcType=VARCHAR}, email=#{email, jdbcType=VARCHAR},
sex=#{sex, jdbcType=VARCHAR}
where id=#{id, jdbcType=INTEGER}
